// src/components/CriarUsuario.tsx
import React, { useState } from "react";
import { criarUsuario, Usuario } from "../api/usuarioApi";

const CriarUsuario: React.FC = () => {
  const [usuario, setUsuario] = useState<Usuario>({
    nome: "",
    email: "",
    CPF: "",
    senha: "",
    saldo: 0,
  });
  const [mensagem, setMensagem] = useState<string>("");

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setUsuario({ ...usuario, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const novoUsuario = await criarUsuario(usuario);
      setMensagem(`Usuário criado com sucesso! ID: ${novoUsuario.id}`);
    } catch (error: any) {
      setMensagem(
        `Erro ao criar usuário: ${error.response?.data || error.message}`
      );
    }
  };

  return (
    <div>
      <h2>Criar Usuário</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="nome"
          placeholder="Nome"
          value={usuario.nome}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="E-mail"
          value={usuario.email}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="cpf"
          placeholder="CPF (11 dígitos)"
          value={usuario.CPF}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="senha"
          placeholder="Senha"
          value={usuario.senha}
          onChange={handleChange}
          required
        />
        <button type="submit">Criar Usuário</button>
      </form>
      {mensagem && <p>{mensagem}</p>}
    </div>
  );
};

export default CriarUsuario;
